import { useEffect,useState, useRef } from 'react'
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Editor from "@monaco-editor/react"
import axios from 'axios';
import './App.css'
import useFuture from './futureBuilder';


function EditArea({id , name}) {

  let files = {
    "script.py": {
      name: "script.py",
      language: "python",
      value: "Here is some python text"
    },
    "code.js": {
      name: "code.js",
      language: "javascript",
      value: "Enter your javascript code"
    }
  }

  const [fileName, setFileName] = useState("script.py"); // change to "index.html"
  const editorRef = useRef(null);
  const file = files[fileName];
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData(name).then(result => {
      setData(result);
      setLoading(false);
    });
  }, [name]);


  async function fetchData(problemName)  {
    try {
  
        const response = await axios.post(`http://localhost:8000/questionSnippets/${problemName}`);
        files['code.js'].value = response.data[6].code;
        files['script.py'].value = response.data[3].code;
        return true;
       
    } catch (error) {
        console.error('Error fetching data:', error);
    }
  };
  
  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
  }


  

  if (loading) {
    return <div>Loading...</div>;
  }

  async function getEditorValue (){
    const formData = {
      text:editorRef.current.getValue(),
      lang:files[fileName].language,
      Qid:id,
      PName: name
    };
    console.log(JSON.stringify(formData));
    try {
      const response = await fetch('http://localhost:8000/solution/checkSol', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
      });

      if (response.ok) {
          console.log('Data sent successfully');
      } else {
          console.error('Failed to send data');
      }
  } catch (error) {
      console.error('Error sending data:', error);
  }
    
  }

    console.log(files);
  return (   
    <div className="App">
      <div>
      <button onClick={() => setFileName("script.py")}>
        Switch to python
      </button>
      <button onClick={() => setFileName("code.js")}>
        Switch to javascript
      </button>
      
      <Editor
        height="70vh"
        width="90vh"
        theme="vs-dark"
        onMount={handleEditorDidMount}
        path={file.name}
        defaultLanguage={file.language}
        defaultValue={file.value}
      />
      </div>
      <button onClick={() => getEditorValue()}>
        Submit
      </button>
    </div>
  )
}

export default EditArea
