import React, { useEffect ,useState,useRef} from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import Sidebar from './sideBar';
import Topbar from './topBar';
import Editor from "@monaco-editor/react"

import FrontPage from './homepage';

var problemName = "";
var problemId = 0;

function EditArea({id , name}) {

 

  const [fileName, setFileName] = useState("script.py"); // change to "index.html"
  const editorRef = useRef(null);
  const file = files[fileName];
 
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data from API
        const response = await axios.get(`http://localhost:8000/questionSnippets/${name}`);
        console.log(response)
        const jsonData = await response.json();
      
        // Update state with fetched data
        setData(jsonData);
        setLoading(false); // Set loading status to false
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); // Set loading status to false even if there's an error
      }
    };
    fetchData(); // Call the fetchData function when component mounts
  }, []);
 
  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
  }

  async function getEditorValue (){
    const formData = {
      text:editorRef.current.getValue(),
      lang:files[fileName].language,
      Qid:id,
      PName: name
    };
    console.log(JSON.stringify(formData));
    try {
      const response = await fetch('http://localhost:8000/solution/checkSol', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
      });

      if (response.ok) {
          console.log('Data sent successfully');
      } else {
          console.error('Failed to send data');
      }
  } catch (error) {
      console.error('Error sending data:', error);
  }
    
  }

  if (loading) {
    return <div>Loading...</div>;
  }
    console.log(files);
  return (   
    <div className="App">
      <div>
      <button onClick={() => setFileName("script.py")}>
        Switch to python
      </button>
      <button onClick={() => setFileName("code.js")}>
        Switch to javascript
      </button>
      
      <Editor
        height="70vh"
        width="90vh"
        theme="vs-dark"
        onMount={handleEditorDidMount}
        path={file.name}
        defaultLanguage={file.language}
        defaultValue={file.value}
      />
      </div>
      <button onClick={() => getEditorValue()}>
        Submit
      </button>
    </div>
  )
}


const fetchData = async (setData) => {
  try {
  
    var num = Math.floor(Math.random()*50);
      //console.log(num);
      const name = await axios.get(`http://localhost:8000/check/${num}`);
      problemName = name.data.titleSlug;
      problemId = name.data.frontendQuestionId;
      console.log(problemName);
      console.log(problemId);
      
      const response = await axios.get(`http://localhost:8000/questionData/${name.data.titleSlug}/${name.data.frontendQuestionId}`);

      setData(response.data);
  } catch (error) {
      console.error('Error fetching data:', error);
  }
};


function FightPage(){
    const [data, setData] = useState(false);

    useEffect(() => {
        fetchData(setData);
    }, []);

    if(data === false){
      return <div>Loading...</div>
    }

    return (
        <div className="app">
        <Topbar />
        <div className='mainArea'>
        <div dangerouslySetInnerHTML={{ __html: data }} className='standardDiv' />
        <div className='standardDiv'><EditArea id={problemId} name={problemName} ></EditArea></div>
        </div>
         </div>
    )
  
}



export default FightPage
