import { useState, useRef } from 'react'
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Editor from "@monaco-editor/react"
import './App.css'

const files = {
  "script.py": {
    name: "script.py",
    language: "python3",
    value: "Here is some python text"
  },
  "code.js": {
    name: "code.js",
    language: "javascript",
    value: "Enter your javascript code"
  }
}

function EditArea({id , name}) {
  const [fileName, setFileName] = useState("script.py"); // change to "index.html"
  const editorRef = useRef(null);
  const file = files[fileName];
  // files["script.py"] -> file -> name, language, value -> pass those to the editor

  // it will take up the full width / height of its container (width: 100%, height: 100%)
  // editors of any size
  // full screen, embed type size

  // 1. Be able to change from 1 file to the next
  // 2. Get the value of the Monaco editor

  // a file path (name), a language, a default value

  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
  }

  async function getEditorValue (){
    const formData = {
      text:editorRef.current.getValue(),
      lang:files[fileName].language,
      Qid:id,
      PName: name
    };
    console.log(JSON.stringify(formData));
    try {
      const response = await fetch('http://localhost:8000/solution/checkSol', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
      });

      if (response.ok) {
          console.log('Data sent successfully');
      } else {
          console.error('Failed to send data');
      }
  } catch (error) {
      console.error('Error sending data:', error);
  }
    
  }

  return (
    
    <div className="App">
      <div>
      <button onClick={() => setFileName("script.py")}>
        Switch to python
      </button>
      <button onClick={() => setFileName("code.js")}>
        Switch to javascript
      </button>
      
      <Editor
        height="vh"
        width="100vh"
        theme="vs-dark"
        onMount={handleEditorDidMount}
        path={file.name}
        defaultLanguage={file.language}
        defaultValue={file.value}
      />
      </div>
      <button onClick={() => getEditorValue()}>
        Submit
      </button>
    </div>
  )
}

export default EditArea
