import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import TSParticles  from 'react-tsparticles';

const FrontPage = () => {
  return (
    
    <StyledFrontPage>
    <TSParticles className="particles" id="tsparticles" init={particlesInit} options={options} />
      <h1>Welcome to LeetFights</h1>
      <p>
        Welcome to the Coding Battle Arena, where two developers face off in a
        thrilling coding competition!
      </p>
      <div className="button-container">
      <Link className="start-button" to="/fight">
  Start Battle
</Link>
        <button className="invite-button">Invite Friend</button>
      </div>
    </StyledFrontPage>
  );
};

const StyledFrontPage = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f0f0f0;
  padding: 20px;
  box-sizing: border-box;

  h1 {
    font-size: 36px;
    margin-bottom: 20px;
  }

  p {
    font-size: 18px;
    margin-bottom: 40px;
  }

  .button-container {
    display: flex;
    justify-content: space-between;
    width: 100%;
    max-width: 400px;
  }

  .start-button,
  .invite-button {
    padding: 10px 20px;
    font-size: 16px;
    cursor: pointer;
  }

  .start-button {
    background-color: #007bff;
    color: #fff;
    border: none;
    border-radius: 5px;
  }

  .invite-button {
    background-color: #6c757d;
    color: #fff;
    border: none;
    border-radius: 5px;
  }
`;

const particlesInit = (engine) => {
    console.log(engine);
  };
  
  const options = {
    fullScreen: {
      enable: true,
      zIndex: -1,
    },
    particles: {
      number: {
        value: 80,
        density: {
          enable: true,
          value_area: 800,
        },
      },
      color: {
        value: '#fff',
      },
      shape: {
        type: 'circle',
        stroke: {
          width: 0,
          color: '#000',
        },
        polygon: {
          nb_sides: 5,
        },
        image: {
          src: 'img/github.svg',
          width: 100,
          height: 100,
        },
      },
      opacity: {
        value: 0.5,
        random: true,
        anim: {
          enable: true,
          speed: 1,
          opacity_min: 0.1,
          sync: false,
        },
      },
      size: {
        value: 5,
        random: true,
        anim: {
          enable: true,
          speed: 4,
          size_min: 0.1,
          sync: false,
        },
      },
      line_linked: {
        enable: false,
        distance: 150,
        color: '#ffffff',
        opacity: 0.4,
        width: 1,
      },
      move: {
        enable: true,
        speed: 6,
        direction: 'none',
        random: true,
        straight: false,
        out_mode: 'out',
        attract: {
          enable: false,
          rotateX: 600,
          rotateY: 1200,
        },
      },
    },
    interactivity: {
      detect_on: 'canvas',
      events: {
        onhover: {
          enable: true,
          mode: 'repulse',
        },
        onclick: {
          enable: true,
          mode: 'push',
        },
        resize: true,
      },
      modes: {
        grab: {
          distance: 400,
          line_linked: {
            opacity: 1,
          },
        },
        bubble: {
          distance: 400,
          size: 40,
          duration: 2,
          opacity: 8,
          speed: 3,
        },
        repulse: {
          distance: 200,
          duration: 0.4,
        },
        push: {
          particles_nb: 4,
        },
        remove: {
          particles_nb: 2,
        },
      },
    },
    retina_detect: true,
  };

export default FrontPage;
